  name: 'ConfigPage',
  props: ['showPage'],
  data() {
    return {
      loading: false,
      errmsg: '',
      retrieveResult: '',
      nrOfTCUs: 0,
      tcu: [{
        name: '',
        ipaddress: '192.168.xxx.xxx'
      }, {
