      }],
      config: {
        host: '',
        port: 0,
        tcu: []
      }
    };
  },
  computed: {},
  methods: {
    validateIP(ix) {
      var ip = String(this.tcu[ix].ipaddress);
      var ipsegs = ip.split(".");
      if (ipsegs.length != 4) {
        return false;
      }
      for (var seg of ipsegs) {
        if (String(seg).match("^[0-9]{1,3}$") == null) return false;
      }
      return true;
    },
    onSubmit() {
      this.config.tcu = [];
      for (var i = 0; i < this.nrOfTCUs; i++) {
        this.config.tcu.push({
          name: this.tcu[i].name,
          ipaddress: this.tcu[i].ipaddress
        });
      }
      this.emitter.emit("config", this.config);
      this.axios.post("/api/command", {
        command: "saveConfig",
        data: this.config
      }).then(response => {
        var f = response.data;
        if (f.error !== undefined) {
          this.errmsg = "danger";
          this.retrieveResult = f.error;
        } else {
          this.errmsg = "success";
          this.retrieveResult = "Configuration saved.";
        }
        this.loading = false;
      }).catch(error => {
        this.retrieveResult = error.message;
        this.errmsg = "danger";
        this.loading = false;
      });
    },
    getConfig() {
      this.axios.post("/api/command", {
        command: "getConfig",
        data: {}
      }).then(response => {
        var f = response.data;
        if (f.error !== undefined) {
          this.errmsg = "danger";
          this.retrieveResult = f.error;
        } else {
          this.errmsg = "success";
          this.retrieveResult = "";
          this.config = f;
          this.tcu = f.tcu;
          this.nrOfTCUs = this.tcu.length;
          for (var i = this.nrOfTCUs - 1; i < 5; i++) {
            this.tcu.push({
              name: '',
              ipaddress: ''
            });
          }
        }
        this.loading = false;
      }).catch(error => {
        this.retrieveResult = error.message;
        this.errmsg = "danger";
        this.loading = false;
      });
    }
  },
  watch: {},
  mounted() {
    this.getConfig();
  }
