  methods: {
    getSomething() {
      this.loading = true;
      this.axios.post("/api/command", {
        command: "getSomething",
        data: {
          what: "a cup of tea, please"
        }
      }).then(response => {
        var f = response.data;
        if (f.error !== undefined) {
          this.errmsg = "danger";
          this.retrieveResult = f.error;
        } else {
          this.errmsg = "success";
          this.retrieveResult = f.message;
        }
        this.loading = false;
      }).catch(error => {
        this.retrieveResult = error.message;
        this.errmsg = "danger";
        this.loading = false;
      });
    },
