import { Line } from 'vue-chartjs';
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);
export default {
  components: {
    Line
  },
  name: 'HomePage',
  props: ['showPage'],
  data() {
    return {
      loading: false,
      errmsg: '',
      retrieveResult: '',
      chartData: {
        labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
        datasets: [{
          label: '',
          borderColor: '#f87979',
          data: [0, 1, 1, 0, 0, 0, 1]
        }]
      },
      chartOptions: [{
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: {
            display: false
          }
        },
        scales: {
          y: {
            ticks: {
              callback: function (value, index, ticks) {
                if (value == 0) return 'line 1';
                return '';
              }
            },
            grid: {
              display: false
            }
          }
        }
      }]
    };
  },
  methods: {
    getTempDataFiles() {
      this.loading = true;
      this.axios.post("/api/command", {
        command: "getTempDataFiles",
        data: {}
      }).then(response => {
        var f = response.data;
        if (f.error !== undefined) {
          this.errmsg = "danger";
          this.retrieveResult = f.error;
        } else {
          this.errmsg = "success";
          this.retrieveResult = f.message;
        }
        this.loading = false;
      }).catch(error => {
        this.retrieveResult = error.message;
        this.errmsg = "danger";
        this.loading = false;
      });
    },
    getSomethingElse() {
      this.loading = true;
      this.axios.post("/api/command", {
        command: "getSomethingElse",
        data: {
          what: "a cup of coffee, please"
        }
      }).then(response => {
        var f = response.data;
        if (f.error !== undefined) {
          this.errmsg = "danger";
          this.retrieveResult = f.error;
        } else {
          this.errmsg = "success";
          this.retrieveResult = f.message;
        }
        this.loading = false;
      }).catch(error => {
        this.retrieveResult = error.message;
        this.errmsg = "danger";
        this.loading = false;
      });
    }
  },
  watch: {},
  mounted() {}
};
