import "core-js/modules/es.array.push.js";
import TcuSetup from './components/TcuSetup.vue';
import Graphs from './components/Graphs.vue';
export default {
  components: {
    TcuSetup,
    Graphs
  },
  name: 'HomePage',
  props: ['showPage'],
  data() {
    return {
      loading: false,
      errmsg: '',
      retrieveResult: ''
    };
  },
  computed: {
    chartHeight() {
      return {
        height: `${700}px`,
        position: 'relative'
      };
    }
  },
  methods: {
    getTcus() {
      console.log("TCUs:", this.config.tcu);
      this.tcus = [];
      for (var t of this.config.tcu) {
        this.tcus.push({
          value: {
            name: t.name,
            ipaddress: t.ipaddress
          },
          text: t.name
        });
      }
    }
  },
  watch: {
    tcu(newValue) {
      if (newValue != undefined) {
        this.getProperties();
        this.emitter.emit("newtcu", newValue);
      }
    }
  },
  mounted() {
    this.emitter.on('config', cfg => {
      this.config = cfg;
      this.showGraph = false;
      this.getTcus();
    });
    this.showGraph = false;
  }
};
