export default {
  components: {},
  name: 'ConfigPage',
  props: ['showPage'],
  data() {
    return {
      loading: false,
      errmsg: '',
      retrieveResult: ''
    };
  },
  computed: {
    validateIP() {
      var ip = String(config.ipaddress);
      var ipsegs = ip.split(".");
      if (ipsegs != 4) {
        return false;
      }
      for (var seg of ipsegs) {
        if (String(seg).match("^[0-9]{1,3}$") == null) return false;
      }
      return true;
    }
  },
  methods: {},
  watch: {},
  mounted() {}
};
