import "core-js/modules/es.array.push.js";
import 'chartjs-adapter-moment';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, TimeScale } from 'chart.js';
import { Line } from 'vue-chartjs';
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, TimeScale);
export default {
  components: {
    Line
  },
  name: 'HomePage',
  props: ['showPage'],
  data() {
    return {
      loading: false,
      errmsg: '',
      retrieveResult: '',
      showDay: false,
      showGraph: false,
      config: {
        tcu: []
      },
      tcus: [{
        value: {
          name: '',
          ipaddress: ''
        },
        text: ''
      }],
      tcu: {
        name: '',
        ipaddress: ''
      },
      devices: [],
      search: '',
      fileNames: [],
      day: '',
      loaded: [false, false, false, false, false, false, false, false, false, false, false, false],
      tempData: {
        labels: [],
        datasets: [{
          label: '',
          borderColor: '#f87979',
          data: []
        }]
      },
      stateData: {
        labels: [],
        datasets: [{
          label: '',
          borderColor: '#f87979',
          data: []
        }]
      },
      chartOptions: {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: {
            display: false
          }
        },
        scales: {
          y: {
            ticks: {
              callback: function () {
                return '';
              }
            },
            grid: {
              display: false
            },
            min: 15,
            max: 35
          },
          x: {
            type: 'time',
            time: {
              unit: 'hour',
              displayFormats: {
                hour: 'HH:mm'
              },
              tooltipFormat: 'HH:mm',
              ticks: {
                source: 'auto'
              }
            }
          }
        }
      }
    };
  },
  computed: {
    chartHeight() {
      return {
        height: `${1000}px`,
        position: 'relative'
      };
    }
  },
  methods: {
    getTcus() {
      console.log("TCUs:", this.config.tcu);
      this.tcus = [];
      for (var t of this.config.tcu) {
        this.tcus.push({
          value: {
            name: t.name,
            ipaddress: t.ipaddress
          },
          text: t.name
        });
      }
      console.log("List of TCUs:", this.tcus);
    },
    getProperties() {
      console.log("getProperties()");
      this.loading = true;
      this.axios.post("/api/command", {
        command: "getProperties",
        data: {
          ipaddress: this.tcu.ipaddress
        }
      }).then(response => {
        var f = response.data;
        if (f.error !== undefined) {
          this.errmsg = "danger";
          this.retrieveResult = f.error;
        } else {
          this.devices = [];
          for (var dev of f.devices) {
            this.devices.push(dev.device);
          }
        }
        console.log("Devices:", this.devices);
        this.loading = false;
      }).catch(error => {
        this.retrieveResult = error.message;
        this.errmsg = "danger";
        this.loading = false;
      });
    },
    getHistDataFiles() {
      console.log("getHistDataFiles()");
      this.loading = true;
      this.axios.post("/api/command", {
        command: "getHistDataFiles",
        data: {
          ipaddress: this.tcu.ipaddress
        }
      }).then(response => {
        var f = response.data;
        if (f.error !== undefined) {
          this.errmsg = "danger";
          this.retrieveResult = f.error;
        } else {
          this.fileNames = [];
          for (var nm of f.files) {
            this.fileNames.push(String(nm).replace("temp_", ""));
          }
        }
        this.loading = false;
      }).catch(error => {
        this.retrieveResult = error.message;
        this.errmsg = "danger";
        this.loading = false;
      });
    },
    getTempDataFile(day) {
      console.log("getTempDataFile(" + day + ")");
      this.loaded[0] = false;
      this.loading = true;
      this.axios.post("/api/command", {
        command: "getTempDataFile",
        data: {
          ipaddress: this.tcu.ipaddress,
          file: 'temp_' + day
        }
      }).then(response => {
        var f = response.data;
        if (f.error !== undefined) {
          this.errmsg = "danger";
          this.retrieveResult = f.error;
        } else {
          this.errmsg = "success";
          this.retrieveResult = "";
          this.cvtTempData(f.content);
        }
        this.loading = false;
      }).catch(error => {
        this.retrieveResult = error.message;
        this.errmsg = "danger";
        this.loading = false;
      });
    },
    cvtTempData(raw) {
      /*
      2023-07-05 14:00:00 start
      2023-07-05 14:00:02 r=0 t=0
      2023-07-06 13:59:03 r=0 t=0
      ...
      2023-07-06 14:00:03 r=0 t=0
      2023-07-06 14:00:02 stop
      */
      this.tempData.datasets[0].data = [];
      var lines = String(raw).split("\n");
      for (var l of lines) {
        var parts = String(l).split(" ");
        if (parts.length > 3) {
          var temp = Number(String(parts[3]).split("=")[1]); // terrarium temperature
          if (temp === 0) temp = 26.0;
          var time = Date.parse(parts[0] + "T" + parts[1]);
          this.tempData.datasets[0].data.push({
            x: time,
            y: temp
          });
        }
      }
      console.log(this.tempData);
      this.loaded[0] = true;
    },
    getStateDataFile(day) {
      console.log("getStateDataFile(" + day + ")");
      this.loading = true;
      this.axios.post("/api/command", {
        command: "getStateDataFile",
        data: {
          ipaddress: this.tcu.ipaddress,
          file: 'state_' + day
        }
      }).then(response => {
        var f = response.data;
        if (f.error !== undefined) {
          this.errmsg = "danger";
          this.retrieveResult = f.error;
        } else {
          this.errmsg = "success";
          this.retrieveResult = "";
          this.cvtStateData(f.content);
        }
        this.loading = false;
      }).catch(error => {
        this.retrieveResult = error.message;
        this.errmsg = "danger";
        this.loading = false;
      });
    },
    cvtStateData(raw) {
      /*
      2023-07-04 17:00:00 start
      2023-07-04 17:00:00 light1 1
      2023-07-04 17:00:00 light2 1
      2023-07-04 17:00:00 light3 1
      2023-07-04 17:00:00 light4 1
      2023-07-04 17:00:00 uvlight 1
      2023-07-04 17:00:00 light6 0
      2023-07-04 17:00:00 pump 0
      2023-07-04 17:00:00 sprayer 0
      2023-07-04 17:00:00 mist 0
      2023-07-04 17:00:00 fan_in 1
      2023-07-04 17:00:00 fan_out 0
      2023-07-04 17:00:00 spare 0
      2023-07-04 20:00:02 pump 1 -1
      2023-07-04 20:05:02 fan_out 1 -1
      2023-07-04 20:15:02 pump 0
      2023-07-04 20:15:02 fan_in 0
      2023-07-04 20:15:02 fan_out 0
      2023-07-04 20:15:02 fan_in 1 -2
      2023-07-05 10:05:02 sprayer 1 10:05:32
      2023-07-06 14:00:02 stop
      */
      for (var i = 0; i < this.devices.length; i++) {
        this.tempData.datasets.push({
          data: []
        });
        this.loaded[i + 1] = false;
      }
      var lines = String(raw).split("\n");
      for (var l of lines) {
        var parts = String(l).split(" ");
        console.log("parts=", parts);
        if (parts.length > 3) {
          var ix = this.getDeviceIndex(parts[2]) + 1;
          console.log("ix=" + ix);
          if (ix !== 0) {
            var time = Date.parse(parts[0] + "T" + parts[1]);
            var value = ix * 2.0 * Number(parts[3]) + 2.0;
            this.tempData.datasets[ix].data.push({
              x: time,
              y: value
            });
          }
        }
      }
      console.log(this.tempData);
      this.loaded[0] = true;
    }
  },
  getDeviceIndex(dev) {
    var ix = -1;
    for (var i = 0; i < this.devices.length; i++) {
      if (dev === this.devices[i]) {
        ix = i;
        break;
      }
    }
    return ix;
  },
  watch: {
    tcu(newValue) {
      console.log("Variable 'tcu' has changed to " + newValue);
      if (newValue != undefined) {
        this.day = '';
        this.getHistDataFiles(newValue);
        this.getProperties();
        this.showDay = true;
      }
    },
    day(newValue) {
      console.log("Variable 'day' has changed to " + newValue);
      if (newValue != '') {
        this.getTempDataFile(newValue);
        this.getStateDataFile(newValue);
        this.showGraph = true;
      }
    }
  },
  mounted() {
    this.emitter.on('config', cfg => {
      console.log("Home page received 'config' message: ", cfg);
      this.config = cfg;
      this.showDay = false;
      this.showGraph = false;
      this.getTcus();
    });
    //    this.getTcus();
    this.showDay = false;
    this.showGraph = false;
  }
};
